/* Write to program to print true if input charatcer 'A' is a Uppercase charater.*/
package NumberCharacterStringClass;

public class CharacterExercise1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'A';
		if(ch >= 'A' &&  ch <= 'Z') {
			System.out.println("True A is Uppercase");
		
		}

	}

}
